// This code processes each item coming into the node.
return items.map(item => {
  // 1. Access the raw text string which contains the JSON data.
  const rawText = item.json.content.parts[0].text;

  // 2. Parse the string into a usable JavaScript object.
  const parsedData = JSON.parse(rawText);

  // 3. Shorten the job description to a maximum of 200 characters.
  if (parsedData.job_description && parsedData.job_description.length > 200) {
    parsedData.job_description = parsedData.job_description.substring(0, 200);
  }

  // 4. Create a new object with only the required fields.
  const result = {
    company_name: parsedData.company_name || "",
    benifits: parsedData.benifits || "",
    job_description: parsedData.job_description || "",
    location: parsedData.location || ""
  };

  // 5. Return the cleaned object in the standard n8n format.
  return { json: result };
});

// This code will process the incoming item to extract the score.
return items.map(item => {
  // 1. Get the text string that contains the JSON with the score.
  const scoreText = item.json.content.parts[0].text;

  // 2. Parse that string into a JavaScript object.
  const parsedData = JSON.parse(scoreText);

  // 3. Extract the 'Score' value from the object.
  const scoreValue = parsedData.Score;

  // 4. Return the score in a new, simple JSON object for the next node.
  return {
    json: {
      score: scoreValue
    }
  };
});

// This code will extract the cover letter from the incoming data.
return items.map(item => {
  // 1. Access the text field containing the JSON string.
  const rawText = item.json.content.parts[0].text;

  // 2. Parse the string into a JavaScript object.
  const parsedData = JSON.parse(rawText);

  // 3. Get the value of the "Cover Letter" key.
  const coverLetter = parsedData["Cover Letter"];

  // 4. Return the extracted cover letter in a new object.
  return {
    json: {
      coverLetter: coverLetter
    }
  };
});
